mapper.select:
/* admin.author.selectTotCntAuthor */
SELECT COUNT(1) TOT_CNT FROM op_author A WHERE 1 = 1
AND A.AUTHOR_NM LIKE '%'||#{sch_auth_nm}|| '%'

/* admin.author.selectPageListAuthor */ SELECT A.AUTHOR_ID , A.AUTHOR_NM , A.RM , A.REGISTER_NO , B.USER_NM AS REGISTER_NM , DATE_FORMAT(A.REGIST_DT, '%Y.%m.%d') AS REGIST_YMD , A.REGIST_DT FROM op_author A LEFT OUTER JOIN op_user B ON A.REGISTER_NO = B.USER_NO WHERE 1 = 1
AND A.AUTHOR_NM LIKE '%'|| #{sch_auth_nm}|| '%'
ORDER BY A.AUTHOR_NM

mapper.insert:
/* admin.author.insertAuthor */

INSERT INTO op_author (
	AUTHOR_ID
	, AUTHOR_NM
	, RM
	, REGISTER_NO
	, REGIST_DT
)
SELECT #{author_id}
	, #{author_nm}
	, #{rm}
	, #{ss_user_no}
	, CURRENT_TIMESTAMP
FROM dual

mapper.update:
/* admin.author.updateAuthor */
UPDATE op_author
SET AUTHOR_NM = #{author_nm}
	, RM = #{rm}
	, UPDUSR_NO = #{ss_user_no}
	, UPDT_DT = CURRENT_TIMESTAMP
WHERE AUTHOR_ID = #{author_id}

mapper.delete:
/* admin.author.deleteAuthor */

DELETE FROM op_author
WHERE AUTHOR_ID = #{author_id}

mapper.select:
/* admin.author.selectExistYnAuthor */

SELECT CASE WHEN COUNT(*) > 0 THEN 'Y' ELSE 'N' END EXIST_YN
FROM op_author
WHERE AUTHOR_ID = #{author_id}
