mapper.select:
/* com.whomade.planfAi.admin.menu.mapper.MenuMapper.selectMenuList */ SELECT MENU_ID, MENU_TY_CODE, MENU_LEVEL, MENU_NM, URL, SORT_ORDR, USE_YN, SORT_ORDR_1, SORT_ORDR_2, SORT_ORDR_3, SORT_ORDR_4, SORT_ORDR_5, SORT_ORDR_6, REGISTER_NO, REGIST_DT, UPDUSR_NO, UPDT_DT FROM op_menu WHERE 1=1
AND USE_YN = #{useYn}
ORDER BY SORT_ORDR_1, SORT_ORDR_2, SORT_ORDR_3, SORT_ORDR_4, SORT_ORDR_5, SORT_ORDR_6

/* com.whomade.planfAi.admin.menu.mapper.MenuMapper.selectParentMenuInfo */
SELECT 
    SORT_ORDR_1, SORT_ORDR_2, SORT_ORDR_3, 
    SORT_ORDR_4, SORT_ORDR_5, SORT_ORDR_6
FROM op_menu 
WHERE MENU_ID = #{parentMenuId}

mapper.insert:
/* com.whomade.planfAi.admin.menu.mapper.MenuMapper.insertMenu */
INSERT INTO op_menu (
    MENU_ID, MENU_TY_CODE, MENU_LEVEL, MENU_NM, URL, SORT_ORDR, USE_YN,
    SORT_ORDR_1, SORT_ORDR_2, SORT_ORDR_3, SORT_ORDR_4, SORT_ORDR_5, SORT_ORDR_6,
    REGISTER_NO, REGIST_DT
) VALUES (
    #{menuId}, #{menuTyCode}, #{menuLevel}, #{menuNm}, #{url}, #{sortOrdr}, #{useYn},
    #{sortOrdr1}, #{sortOrdr2}, #{sortOrdr3}, #{sortOrdr4}, #{sortOrdr5}, #{sortOrdr6},
    #{registerNo}, CURRENT_TIMESTAMP
)
